/*Table structure for table `notice_info` */

DROP TABLE IF EXISTS `notice_info`;

CREATE TABLE `notice_info` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `title` varchar(200) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '標題',
  `content` varchar(2000) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '内容',
  `resource_table` varchar(50) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '來源表',
  `resource_id` int(20) DEFAULT NULL COMMENT '來源表ID',
  `mem_id` int(11) DEFAULT NULL COMMENT '會員ID',
  `create_date` date DEFAULT NULL COMMENT '創建時間',
  `see_date` date DEFAULT NULL COMMENT '查看時間',
  `is_see` char(10) COLLATE utf8mb4_unicode_ci DEFAULT 'no' COMMENT '是否查看',
  `pic_url` varchar(200) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '图片',
  PRIMARY KEY (`id`)
